CREATE DATABASE TOURFIESTA;
-- DROP DATABASE TOURFIESTA;
USE TOURFIESTA;

CREATE TABLE UserInfo(
    UserID INT PRIMARY KEY,
    Username VARCHAR(20) NOT NULL,
    Password VARCHAR(45) NOT NULL,
    Email VARCHAR(100) UNIQUE NOT NULL,
    name VARCHAR(20) NOT NULL,
    Address VARCHAR(200) NOT NULL,
    Phone VARCHAR(20) UNIQUE NOT NULL
);


SELECT * FROM UserInfo;

SELECT COUNT(UserID) as Total_Users FROM UserInfo;

CREATE TABLE Destinations(
    Destinationid INT PRIMARY KEY,
    DestinationName VARCHAR(20),
    Location VARCHAR(100),
    type VARCHAR(45),
    description TEXT,
    popular_attraction VARCHAR(50),
    climate VARCHAR(100),
    Average_cost BIGINT,
    rating FLOAT,
    UserID INT,
    FOREIGN KEY(UserID) REFERENCES UserInfo(UserID)
);


SELECT COUNT(*) AS Total_destination_count FROM Destinations;

SELECT * FROM Destinations WHERE DestinationName = 'Coorg';

SELECT climate, COUNT(*) FROM Destinations GROUP BY climate;


SELECT * FROM Destinations
GROUP BY Destinationid
HAVING rating>4.5
ORDER BY rating DESC;

-- for Visited places - To recommend last visited destinations
SELECT d.DestinationName FROM UserInfo u
INNER JOIN Destinations d
ON u.UserID = d.UserID
WHERE UserName = 'neha_agarwal';

CREATE TABLE Accommodation_Booking(
    booking_id INT PRIMARY KEY,
	UserID INT,
    accommodationID VARCHAR(20),
    bookingdate DATE,
    arrival_date DATE,
    departure_Date DATE,
    total_amount DECIMAL(10,2),
    status VARCHAR(10),
    booking_time TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (UserID) REFERENCES UserInfo(UserID),
    UNIQUE(UserID, bookingdate)
);


SELECT City, COUNT(*) FROM Accommodation GROUP BY City;

SELECT COUNT(*) AS High_Rated_Accommodations 
FROM Accommodation 
WHERE rating > 4.0;

SELECT COUNT(*) AS Available_Accommodations 
FROM Accommodation 
WHERE availablerooms > 0;

SELECT * FROM Accommodation_Booking 
WHERE bookingdate BETWEEN '2024-01-01'AND '2024-01-30'
ORDER BY booking_id;

SELECT * FROM Accommodation_Booking 
WHERE accommodationID = 'BLR001'
ORDER BY booking_id, booking_time, bookingdate;

SELECT * FROM Accommodation_Booking
WHERE accommodationID = 'BLR001' AND status = 'confirmed';

SELECT bookingdate, COUNT(status) no_of_confirmed_bookings FROM Accommodation_Booking
WHERE accommodationID = 'BLR001' AND status = 'Confirmed' AND bookingdate BETWEEN '2024-10-01' AND '2024-10-31'
GROUP BY bookingdate;

CREATE TABLE Accommodation (
    accommodation_id VARCHAR(20) PRIMARY KEY,
	UserID INT,
    booking_id INT,
    Destinationid INT, 
    name_of_hotel VARCHAR(45),
    City VARCHAR(100),
    Locality VARCHAR(100),
    stars TINYINT NOT NULL, 
    rating DECIMAL(3, 2) NOT NULL,  
    checkInDate DATE NOT NULL,
    checkOutDate DATE NOT NULL,
    pricepernight DECIMAL(10, 2),
    availablerooms INT,
    wifi VARCHAR(5), 
    swimmingpool VARCHAR(5),
    parking VARCHAR(5),
    restaurant VARCHAR(5),
    laundry VARCHAR(5),
    cafe VARCHAR(5),
    mainImage VARCHAR(100) NOT NULL,
    FOREIGN KEY (UserID) REFERENCES UserInfo(UserID),
    FOREIGN KEY (booking_id) REFERENCES Accommodation_Booking(booking_id),
    FOREIGN KEY (Destinationid) REFERENCES Destinations(Destinationid)
);


SELECT COUNT(*) FROM Accommodation WHERE City = 'Bangalore';

SELECT COUNT(DISTINCT City) FROM Accommodation;

SELECT City, COUNT(*) AS Total_Accommodations 
FROM Accommodation 
GROUP BY City;


SELECT COUNT(*) 
FROM Accommodation 
WHERE wifi IS NULL;

SELECT COUNT(wifi) FROM Accommodation
WHERE wifi = 'Yes';

SELECT COUNT(*) 
FROM Accommodation A 
WHERE A.stars >= 4;

SELECT City, (
	SELECT COUNT(*) FROM UserInfo WHERE City = A.City
    ) AS Total_Users 
FROM Accommodation A;

SELECT MAX(a.pricepernight) as pricepernight, a.* FROM Accommodation a
WHERE name_of_hotel = 'Keys Select Hotel'
GROUP BY accommodation_id;

SELECT u.*,b.*,a.*
FROM UserInfo u
INNER JOIN Accommodation_Booking b ON u.UserID = b.UserID 
INNER JOIN Accommodation a ON b.accommodationID = a.accommodation_id
WHERE a.accommodation_id = 'BLR001'
AND b.status = 'Confirmed';

CREATE TABLE Activity(
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(50),
    NameOfHotel VARCHAR(50),
    location VARCHAR(100),
    date DATE,
    time TIME,
    price BIGINT,
    capacity_of_num_of_participants INT,
    Destinationid INT,
    FOREIGN KEY (Destinationid) REFERENCES Destinations(Destinationid),
    UNIQUE(NameOfHotel, location)
);


SELECT COUNT(*) 
FROM Activity 
WHERE DestinationID = 5;

SELECT COUNT(*) 
FROM Activity 
WHERE DestinationID = 5;

SELECT * FROM Activity
WHERE Destinationid = 5;

CREATE TABLE FlightBookings(
	FlightbookingID INT(10) PRIMARY KEY,
	username VARCHAR(50),
    date VARCHAR(50),
    cancelled VARCHAR(20) ,
    origin VARCHAR(50),
    destination VARCHAR(50),
    passengers INT(10),
    type VARCHAR(50)
);


SELECT * FROM FlightBookings
WHERE FlightbookingID = 97;

SELECT * FROM FlightBookings
WHERE date = '05-11-2017';

CREATE TABLE Flight(
    flight_id VARCHAR(10) PRIMARY KEY,
    airline VARCHAR(30),
    Destination_airport VARCHAR(30),
    distance INT(10),
    price DECIMAL(10,2),
    class VARCHAR(10),
    available_seats INT,
    Departure_TIME VARCHAR(10),
    Arrival_Time VARCHAR(10),
    Operator VARCHAR(25),
    origin_code VARCHAR(10),
    destination_code VARCHAR(10),
    refundable VARCHAR(40),
    noOfBookings INT(10)
);



SELECT * FROM Flight
WHERE airline = 'Pune' AND Destination_airport = 'Chennai' AND refundable = 'Refundable'
ORDER BY Departure_TIME,price;

SELECT COUNT(class) as BusinessClassCount FROM Flight
WHERE class = 'Business' AND airline = 'Guwahati' AND Destination_airport = 'Kolkata';

SELECT status, COUNT(*) AS Total_Flights 
FROM Flight 
GROUP BY status;


CREATE TABLE Payment_Accommodation (
    payment_id INT PRIMARY KEY,
    accommodation_id VARCHAR(20),
    amount DECIMAL(10,0),
    payment_date DATE,
    FOREIGN KEY (accommodation_id) REFERENCES Accommodation(accommodation_id)
);


SELECT COUNT(*) 
FROM Payment_Accommodation;

SELECT MONTH(payment_date) AS Month, COUNT(*) AS Total_Payments 
FROM Payment_Accommodation 
GROUP BY MONTH(payment_date);

CREATE TABLE Payment_Flight (
    payment_id INT PRIMARY KEY,
    flight_id VARCHAR(20),
    amount DECIMAL(10,0),
    payment_date DATE,
    FOREIGN KEY (flight_id) REFERENCES Flight(flight_id)
);

SELECT flight_id, COUNT(*) AS Total_Payments 
FROM Payment_Flight 
GROUP BY flight_id;

CREATE TABLE Payment_Activity (
    payment_id INT PRIMARY KEY,
    activity_id INT,
    amount DECIMAL(10,0),
    payment_date DATE,
    FOREIGN KEY (activity_id) REFERENCES Activity(activity_id)
);



CREATE TABLE Languages(
    LanguageID INT PRIMARY KEY,
    LanguageName VARCHAR(20)
);


SELECT COUNT(*) 
FROM Languages;

CREATE TABLE Categories(
    CategoryID INT PRIMARY KEY,
    Category VARCHAR(100)
);


CREATE TABLE Location(
    LocationID INT PRIMARY KEY,
    Location VARCHAR(100)
);


CREATE TABLE TourGuide (
    GuideID INT PRIMARY KEY,     
    GuideName VARCHAR(255),                      
    Biography TEXT,                              
    Availability DATE,                          
    ContactInfo VARCHAR(255),                                            
    PhotoUrl VARCHAR(255),                       
    ToursLed TEXT,                               
    Training TEXT,                             
    Pricing DECIMAL(10, 2),
    LanguageID INT, 
    FOREIGN KEY (LanguageID) REFERENCES Languages(LanguageID),
    LocationID INT,
    FOREIGN KEY (LocationID) REFERENCES Location(LocationID)
);


SELECT GuideID, COUNT(*) AS Tours_Conducted 
FROM TourGuide 
GROUP BY GuideID;

CREATE TABLE Guide_Language (
    GuideID INT,
    LanguageID INT,
    PRIMARY KEY (GuideID, LanguageID),
    FOREIGN KEY (GuideID) REFERENCES TourGuide(GuideID),
    FOREIGN KEY (LanguageID) REFERENCES Languages(LanguageID)
);


CREATE TABLE TourByInterest (
    TourID INT PRIMARY KEY,       
    InterestCategory VARCHAR(100),               
    TourName VARCHAR(255),                      
    Description TEXT,                          
    Duration INT,                                
    Location VARCHAR(100),                       
    Price DECIMAL(10, 2),                        
    Highlights TEXT,                           
    Itinerary TEXT,                             
    DifficultyLevel ENUM( "Easy", "Moderate", "Difficult"), 
    GroupSizeMin INT,                            
    GroupSizeMax INT,                            
    Availability DATE,                          
    Inclusions TEXT,                             
    Exclusions TEXT,                           
    BookingInfo TEXT,
    LocationID INT, 
    FOREIGN KEY (LocationID) REFERENCES Location(LocationID),
    CategoryID INT, 
    FOREIGN KEY (CategoryID) REFERENCES Categories(CategoryID),
    GuideID INT, 
    FOREIGN KEY (GuideID) REFERENCES TourGuide(GuideID),
    accommodation_id VARCHAR(20),
    FOREIGN KEY (accommodation_id) REFERENCES Accommodation(accommodation_id)
);


SELECT COUNT(TourID) FROM TourByInterest
WHERE InterestCategory = 'Cultural';

SELECT l.Location, COUNT(t.CategoryID) as countOfDestination FROM TourByInterest t
INNER JOIN Location l
ON t.LocationID = l.LocationID
GROUP BY t.LocationID;

SELECT l.Location, COUNT(t.CategoryID) as countOfDestination FROM TourByInterest t
INNER JOIN Location l
ON t.LocationID = l.LocationID
GROUP BY t.LocationID;

SELECT t.Location, c.Category FROM Categories c
LEFT JOIN TourByInterest t
ON c.CategoryID = t.CategoryID;

CREATE TABLE Review(
    review_id INT PRIMARY KEY,
    review_text TEXT,
    rating FLOAT,
    review_date DATE,
    UserID INT,
    Destinationid INT,
    accommodation_id VARCHAR(20),
    activity_id INT,
    TourID INT,
    FOREIGN KEY (UserID) REFERENCES UserInfo(UserID),
    FOREIGN KEY (Destinationid) REFERENCES Destinations(Destinationid),
    FOREIGN KEY (accommodation_id) REFERENCES Accommodation(accommodation_id),
    FOREIGN KEY (activity_id) REFERENCES Activity(activity_id),
    FOREIGN KEY (TourID) REFERENCES TourByInterest(TourID)
);


SELECT Location,review_text, rating, review_date FROM Review
INNER JOIN Location
ON Review.Destinationid = Location.LocationID
WHERE Destinationid IN (
	SELECT LocationID FROM Location
)
ORDER BY rating DESC;
